mapper.select:
SELECT u.*, r.role_name roleName, CONCAT(p.menu_name, p.permission_name) permissionName FROM ( SELECT id userId, username username, nickname nickname, role_id roleId, delete_status deleteStatus, create_time createTime, update_time updateTime FROM sys_user WHERE delete_status = '1' ORDER BY id
) u LEFT JOIN sys_role r ON r.id = u.roleId LEFT JOIN sys_role_permission rp ON rp.role_id = r.id LEFT JOIN sys_permission p ON p.id = rp.permission_id
ORDER BY u.userId

select count(0)
 from sys_user
 WHERE username=#{username}
 AND delete_status='1';

mapper.update:
UPDATE sys_user
SET
nickname = #{nickname}

    , password = #{password}


, role_id = #{roleId}
, delete_status = #{deleteStatus}
WHERE id = #{userId} and id != 10001
